import React, { useState } from 'react';
import { connect } from 'react-redux';
import { Link, Redirect } from 'react-router-dom';
import { signup } from '../actions/auth';

import * as TiIcons from "react-icons/ti";
import * as FaIcons from "react-icons/fa";


const Register = ({ signup, isAuthenticated }) => {
    const [formData, setFormData] = useState({
        username: '',
        first_name: '',
        last_name: '',
        phone: '',
        address: '',
        email: '',
        password: '',
        re_password: '',

    });

    const [accountCreated, setAccountCreated] = useState(false);

    const { username,first_name,last_name,phone,address, email, password, re_password } = formData;

    const onChange = e => setFormData({ ...formData, [e.target.name]: e.target.value });

    const onSubmit = e => {
        e.preventDefault();

        if (password === re_password) {

            signup({ username,first_name,last_name,phone,address, email, password, re_password });
            setAccountCreated(true);
        }
    };

    if (isAuthenticated)
        return <Redirect to='/' />;
    if (accountCreated)
        return <Redirect to='login' />;


    return (
        <div className="login">
                <div class="breadcrumb-option">
                <div class="container">
                <div class="row">
                    <div class="col-lg-12">
                        <div class="breadcrumb__links">
                        <Link to='/'><TiIcons.TiHome className="homeIcon"/> Home</Link>
                        <FaIcons.FaAngleRight/>
                            <span>Register</span>
                        </div>
                    </div>
                </div>
                </div>
            </div>  









            <section class="checkout spad">
            <div class="container">

                <form class="checkout__form" onSubmit={e => onSubmit(e)}>
                    <div class="row">
                        <div class="col-lg-12">
                            <h5>Register </h5>
                            <div class="row">








                                <div class="col-lg-12">                                


                                    <div class="checkout__form__input">
                                        <p>Username <span>*</span></p>
                                        <input
                                            type='text'                                          
