import React,{useState, useEffect} from 'react'
import * as load_order_actionsand_product  from '../actions/prostore';
import { connect } from 'react-redux';
import { useDispatch } from 'react-redux';
import { Link, Redirect } from 'react-router-dom';

import * as AiIcons from 'react-icons/ai';
import * as TiIcons from "react-icons/ti";
import * as FaIcons from "react-icons/fa";
import * as RiIcons from "react-icons/ri";
import * as BsIcons from "react-icons/bs";
import Background from './img/product-1.jpg';

import axios from 'axios';




function Orders({Ordersitem,User,isAuthenticated,Orders,Products}) {



    // const order=[]





    // const config = {
    //     headers: {
    //         'Content-Type': 'application/json'
    //     }
    // };    

    // const k =[]
    // axios.get(`${process.env.REACT_APP_API_URL}/api/order`, config)
    //   .then(function (response) {
    //     let data = response;

    //     console.log(data);
    //   })






    const dispatch = useDispatch()    

    const getOrderAndDetail =async()=>{       

        await dispatch(load_order_actionsand_product.load_order())
        await dispatch(load_order_actionsand_product.load_order_item_detail())
        await dispatch(load_order_actionsand_product.load_product())

    }

    // console.log(Products);

    // console.log(User);
    console.log(Orders);
    useEffect(()=>
    {  
        getOrderAndDetail();  



    },[])


    if (!isAuthenticated)
        return <Redirect to='/login' />;

    // console.log(Products);















    var userOrderHistory=[];

    for(var i=0;i<Orders.length;i++){
        if(Orders[i].user==User["id"]){

            userOrderHistory.push(Orders[i])
        }
    }

    console.log(userOrderHistory);

    for(var i=0;i<userOrderHistory.length;i++){

        var orderid = userOrderHistory[i].order_id;
        const filteredOderByItems = Ordersitem.filter(item => item.order_id === orderid);


        if(filteredOderByItems.length!==0){                        
            var listProd= [];
            for(var j=0;j<filteredOderByItems.length;j++){                           
                var subprod= {};            
                for(var p=0;p<Products.length;p++){
                    // console.log(Products[p].image);
                    if(Products[p].id===filteredOderByItems[j]["products"]){
                        subprod["title"]=Products[p].title;
                        subprod["price"]=Products[p].price;
                        subprod["image"]=Products[p].image;

                        subprod["qty"]=filteredOderByItems[j]["qty"];                        
                        listProd.push(subprod);
                    }



                }



            }            
            userOrderHistory[i]["productDetail"]=listProd
        }


    }
    // console.log(userOrderHistory);


    const filteredItems = userOrderHistory.filter(item => item.order_id === "order_GNyWYL8VF73lja")

    // console.log(filteredItems)
    // console.log(userOrderHistory)    

    return (
        <div className="products">                       
            <div class="breadcrumb-option">
                <div class="container">
                    <div class="row">
                        <div class="col-lg-12">
                            <div class="breadcrumb__links">
                                <Link to='/'><TiIcons.TiHome className="homeIcon"/> Home</Link>
                                <FaIcons.FaAngleRight/>
                                <span>Shop</span>
                            </div>
                        </div>
                    </div>
                </div>
            </div>



            <div>
            {userOrderHistory.map((userOrderHistory, index) => (
                <div key={index} >
                <h6>{userOrderHistory.order_id}-----------------{userOrderHistory.payment_id}-------{userOrderHistory.total}----------{userOrderHistory.date_ordered}------------------------------------------------</h6>

               {
                userOrderHistory.productDetail.map((product,id) =>(
                    <div key={id}>
                        <h4>{product.title}</h4>
                        <h4>{product.image}</h4>
                        <h4>{product.price}</h4>
                        <h4>{product.qty}</h4>

                    </div>
                )


                )}




                </div>
                ))}


            </div>











        </div>
    )
}

// export default Products


const mapStateToProps = ({prostore,auth}) => ({
    Ordersitem: prostore.Ordersitem,
    Orders: prostore.Orders,
    isAuthenticated: auth.isAuthenticated,
    User:auth.user,
    Products:prostore.Products




});


export default connect(mapStateToProps)(Orders);
